// Fix scheduling logic with correct property name
module.exports.onLoad = ({ api }) => {
  console.log(chalk.green.bold('âœ… AutoSent Funny-Love Module Loaded (BD Time)'));

  messages.forEach(({ timer, message }) => {
    const [hour, minute, second, period] = timer.split(/[: ]/);
    let hour24 = parseInt(hour);
    if (period === "PM" && hour !== "12") hour24 += 12;
    if (period === "AM" && hour === "12") hour24 = 0;

    const rule = new schedule.RecurrenceRule();
    rule.tz = 'Asia/Dhaka';
    rule.hour = hour24;
    rule.minute = parseInt(minute);

    schedule.scheduleJob(rule, () => {
      if (!global.data?.allThreadID) return;
      global.data.allThreadID.forEach(threadID => {
        api.sendMessage(message, threadID, (err) => {
          if (err) console.error(`âŒ Error sending message to ${threadID}:`, err);
        });
      });
    });
  });
};

